function #$fieldprefix#_addItem()
{
var itemdiv = document.getElementById('#$fieldprefix#_items');
// Get a ID value for the next item and increment the last item ID
var lastitemfield = document.getElementById('#$fieldprefix#_lastitem');
var lastitem = lastitemfield.value;
lastitem++;
lastitemfield.value = lastitem;
// Add the next item number to the list of items
var itemsdisplayedfield = document.getElementById('#$fieldprefix#_itemsdisplayed');
var itemsdisplayed = itemsdisplayedfield.value;
itemsdisplayed = itemsdisplayed + ',' + lastitem;
itemsdisplayedfield.value = itemsdisplayed;
// Add a new item named item(i)
var divIdName = "#$fieldprefix#_item" + lastitem;
var newdiv = document.createElement('div');
newdiv.setAttribute("id",divIdName);
// Replace the placeholder text with the new fieldprefix
var template = document.getElementById('#$fieldprefix#_itemtemplate');
var newhtml = template.innerHTML;
var regex = new RegExp ('dummyfieldprefix', 'gi') ;
var prefix = document.getElementById('#$fieldprefix#_fieldprefix').value;
newhtml = newhtml.replace(regex,lastitem+"_"+prefix);
// Create the HTML for the new item, add a remove button and insert the new item
newdiv.innerHTML =newhtml+"<div class=\"xar-form-input-wrapper-after\"><input type=\"button\" title=\"#xarML('Remove')# #$label#\" value=\"#xarML('Remove')# #$label#\" style=\"height: 23px; font-size: small;\" onclick=\"javascript:#$fieldprefix#_removeItem(\'"+divIdName+"\')\"></div>";

itemdiv.appendChild(newdiv);
}
function #$fieldprefix#_removeItem(divID)
{
var d = document.getElementById('#$fieldprefix#_items');
var olddiv = document.getElementById(divID);
d.removeChild(olddiv);
var itemsdisplayedfield = document.getElementById('#$fieldprefix#_itemsdisplayed');
var itemsdisplayed = itemsdisplayedfield.value;
var divnum = divID.replace('#$fieldprefix#_item','');
itemsdisplayed = itemsdisplayed.replace(',' + divnum, '');
itemsdisplayedfield.value = itemsdisplayed;
}
