$(document).ready(function(){
$(function() {
var zoomFunc = function(dimension) {
return dimension * 2.5;
}
var shrinkFunc = function(dimension) {
return dimension * 0.3;
}
$('div.gallery img').slidingGallery({'slideSpeed':'slow','Lzoom':zoomFunc, 'Pzoom':zoomFunc,'Lshrink':shrinkFunc,'Pshrink':shrinkFunc});
});
});

$(document).ready(function(){
$(function() {
var zoomFunc = function(dimension) {
return dimension * 2.5;
}
var shrinkFunc = function(dimension) {
return dimension * 0.3;
}
$('div.gallery img').slidingGallery({'slideSpeed':'slow','Lzoom':zoomFunc, 'Pzoom':zoomFunc,'Lshrink':shrinkFunc,'Pshrink':shrinkFunc, 'Lwidth':400, 'Pwidth':400, 'Lheight':270, 'Pheight':270, 'useCaptions':'true', 'container': $('div.gallery')});
});
});

$(document).ready(function(){
$(function() {
var zoomFunc = function(dimension) {
return dimension * 2.5;
}
var shrinkFunc = function(dimension) {
return dimension * 0.3;
}
$('div.gallery img').slidingGallery({
'slideSpeed':'slow',
'Lzoom':zoomFunc,
'Pzoom':zoomFunc,
'Lshrink':shrinkFunc,
'Pshrink':shrinkFunc,
'Lwidth':400,
'Pwidth':400,
'Lheight':270,
'Pheight':270});
});
});
function showCaption(caption) {
if (caption != "") {
document.getElementById('showCaption').innerHTML = caption;
}
else {
if (document.getElementById('currentlocale').value == "de_DE.utf-8") {
document.getElementById('showCaption').innerHTML = "Antragsformular für ice.attest";
}
else {
document.getElementById('showCaption').innerHTML = "Checklist Emergency Plan";
}
}
}

