$(document).ready(function(){
$(function() {
var zoomFunc = function(dimension) {
return dimension * 2.5;
}
var shrinkFunc = function(dimension) {
return dimension * 0.3;
}
$('div.gallery img').slidingGallery({'slideSpeed':'slow','Lzoom':zoomFunc, 'Pzoom':zoomFunc,'Lshrink':shrinkFunc,'Pshrink':shrinkFunc, 'Lwidth':400, 'Pwidth':400, 'Lheight':270, 'Pheight':270});
});
});
